/**
 * In-memory simulator for crowdfunding when REACT_APP_SIMULATOR_MODE=true.
 * No blockchain required - useful for demo/testing.
 */

const SAMPLE_CAMPAIGNS = [
  { id: 0, goal: '5', raised: '0', deadline: 0, funded: false, withdrawn: false },
  { id: 1, goal: '10', raised: '0', deadline: 0, funded: false, withdrawn: false },
  { id: 2, goal: '3', raised: '0', deadline: 0, funded: false, withdrawn: false },
  { id: 3, goal: '8', raised: '0', deadline: 0, funded: false, withdrawn: false },
];

export const SIMULATOR_ACCOUNTS = [
  { address: '0xaaa...111', name: 'Alice' },
  { address: '0xbbb...222', name: 'Bob' },
  { address: '0xccc...333', name: 'Carol' },
];

const INITIAL_BALANCE = '10'; // ETH per account

let state = {
  campaigns: SAMPLE_CAMPAIGNS.map((c) => ({
    ...c,
    deadline: Math.floor(Date.now() / 1000) + 30 * 24 * 60 * 60,
  })),
  backerAmounts: {}, // { campaignId: { address: amount } }
  balances: {}, // address -> balance in ETH string
};

export function resetState() {
  state = {
    campaigns: SAMPLE_CAMPAIGNS.map((c) => ({
      ...c,
      raised: '0',
      funded: false,
      withdrawn: false,
      deadline: Math.floor(Date.now() / 1000) + 30 * 24 * 60 * 60,
    })),
    backerAmounts: {},
    balances: {},
  };
}

export function resetSimulator(activeAddress) {
  resetState();
  if (activeAddress) ensureBalance(activeAddress);
}

export function getSimulatedCampaigns(doReset = false) {
  if (doReset) resetState();
  return state.campaigns.map((c) => ({
    creator: '0x0000000000000000000000000000000000000001',
    goal: c.goal,
    raised: c.raised,
    deadline: c.deadline,
    funded: c.funded,
    withdrawn: c.withdrawn,
  }));
}

export function getSimulatedCampaign(id) {
  const c = state.campaigns[id];
  if (!c) return null;
  return {
    creator: '0x0000000000000000000000000000000000000001',
    goal: c.goal,
    raised: c.raised,
    deadline: c.deadline,
    funded: c.funded,
    withdrawn: c.withdrawn,
  };
}

export function getSimulatedBalance(address) {
  ensureBalance(address);
  return state.balances[address];
}

export function getSimulatedBackerAmount(campaignId, address) {
  const byCampaign = state.backerAmounts[campaignId];
  return byCampaign?.[address] || '0';
}

export function simulatorInvest(campaignId, fromAddress, amountEth) {
  ensureBalance(fromAddress);
  const bal = parseFloat(state.balances[fromAddress]);
  const amt = parseFloat(amountEth);
  if (amt <= 0 || amt > bal) throw new Error('Insufficient balance or invalid amount');

  const c = state.campaigns[campaignId];
  if (!c) throw new Error('Campaign not found');
  if (c.funded) throw new Error('Campaign already funded');
  if (Date.now() / 1000 > c.deadline) throw new Error('Campaign ended');

  state.balances[fromAddress] = String(bal - amt);
  const raised = parseFloat(c.raised) + amt;
  c.raised = String(raised);
  if (raised >= parseFloat(c.goal)) c.funded = true;

  if (!state.backerAmounts[campaignId]) state.backerAmounts[campaignId] = {};
  const prev = parseFloat(state.backerAmounts[campaignId][fromAddress] || '0');
  state.backerAmounts[campaignId][fromAddress] = String(prev + amt);
}
